#Project euler problem 8
chars = list("")
def product_next_13(i):
    return int(chars[i])*int(chars[i+1])*int(chars[i+2])*int(chars[i+3])*int(chars[i+4])*int(chars[i+5])*int(chars[i+6])*int(chars[i+7])*int(chars[i+8])*int(chars[i+9])*int(chars[i+10])*int(chars[i+11])*int(chars[i+12])

#range found experimentially
highest = 0
for i in range(988):
        if product_next_13(i) > highest:
            highest = product_next_13(i)
print(highest)
